            throw new ArgumentsRequired ($this->id . ' cancelOrder () requires a $symbol argument');
        $this->load_markets();
        $market = $this->market ($symbol);
        $response = $this->privatePostCancelOrder (array_merge (array (
            'coin_pair' => $market['id'],
            'order_id' => $id,
        ), $params));
        return $this->parse_order($response['response_data']['order'], $market);
    }

    public function parse_order_status ($status) {
        $statuses = array (
            '2' => 'open',
            '3' => 'canceled',
            '4' => 'closed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order ($order, $market = null) {
        $id = $this->safe_string($order, 'order_id');
        $side = null;
        if (is_array ($order) && array_key_exists ('order_type', $order))
            $side = ($order['order_type'] === 1) ? 'buy' : 'sell';
        $status = $this->parse_order_status($this->safe_string($order, 'status'));
        $symbol = null;
        if ($market === null) {
            $marketId = $this->safe_string($order, 'coin_pair');
            $market = $this->safe_value($this->markets_by_id, $marketId);
        }
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $timestamp = $this->safe_integer($order, 'created_timestamp');
        if ($timestamp !== null) {
            $timestamp = $timestamp * 1000;
        }
        $fee = array (
            'cost' => $this->safe_float($order, 'fee'),
            'currency' => $market['quote'],
        );
        $price = $this->safe_float($order, 'limit_price');
        // $price = $this->safe_float($order, 'executed_price_avg', $price);
        $average = $this->safe_float($order, 'executed_price_avg');
        $amount = $this->safe_float($order, 'quantity');
        $filled = $this->safe_float($order, 'executed_quantity');
        $remaining = $amount - $filled;
        $cost = $amount * $average;
        $lastTradeTimestamp = $this->safe_integer($order, 'updated_timestamp');
        if ($lastTradeTimestamp !== null) {
            $lastTradeTimestamp = $lastTradeTimestamp * 1000;
        }
        $result = array (
            'info' => $order,
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601 ($timestamp),
            'lastTradeTimestamp' => $lastTradeTimestamp,
            'symbol' => $symbol,
            'type' => 'limit',
            'side' => $side,
            'price' => $price,
            'cost' => $cost,
            'average' => $average,
            'amount' => $amount,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => $fee,
            'trades' => null, // todo parse trades (operations)
        );
        return $result;
    }

    public function fetch_order ($id, $symbol = null, $params = array ()) {
        if ($symbol === null)
            throw new ArgumentsRequired ($this->id . ' fetchOrder () requires a $symbol argument');
        $this->load_markets();
        $market = $this->market ($symbol);
        $response = null;
        $response = $this->privatePostGetOrder (array_merge (array (
            'coin_pair' => $market['id'],
            'order_id' => intval ($id),
        ), $params));
        return $this->parse_order($response['response_data']['order']);
    }

    public function withdraw ($code, $amount, $address, $tag = null, $params = array ()) {
        $this->check_address($address);
        $this->load_markets();
        $currency = $this->currency ($code);
        $request = array (
            'coin' => $currency['id'],
            'quantity' => sprintf ('%.10f', $amount),
            'address' => $address,
        );
        if ($code === 'BRL') {
            $account_ref = (is_array ($params) && array_key_exists ('account_ref', $params));
            if (!$account_ref) {
                throw new ExchangeError ($this->id . ' requires $account_ref parameter to withdraw ' . $code);
            }
        } else if ($code !== 'LTC') {
            $tx_fee = (is_array ($params) && array_key_exists ('tx_fee', $params));
            if (!$tx_fee) {
                throw new ExchangeError ($this->id . ' requires $tx_fee parameter to withdraw ' . $code);
            }
            if ($code === 'XRP') {
                if ($tag === null) {
                    if (!(is_array ($params) && array_key_exists ('destination_tag', $params))) {
                        throw new ExchangeError ($this->id . ' requires a $tag argument or destination_tag parameter to withdraw ' . $code);
                    }
                } else {
                    $request['destination_tag'] = $tag;
                }
            }
        }
        $response = $this->privatePostWithdrawCoin (array_merge ($request, $params));
        return array (
            'info' => $response,
            'id' => $response['response_data']['withdrawal']['id'],
        );
    }

    public function parse_ohlcv ($ohlcv, $market = null, $timeframe = '1m', $since = null, $limit = null) {
        return array (
            $ohlcv->timestamp * 1000,
            floatval ($ohlcv->open),
            floatval ($ohlcv->high),
            floatval ($ohlcv->low),
            floatval ($ohlcv->close),
            floatval ($ohlcv->volume),
        );
    }

    public function fetch_ohlcv ($symbol, $timeframe = '1m', $since = null, $limit = null, $params = array ()) {
        $this->load_markets();
        $market = $this->market ($symbol);
        $request = array (
            'precision' => $this->timeframes[$timeframe],
            'coin' => strtolower ($market->id),
        );
        if ($since !== null) {
            $request['from'] = intval ($since / 1000);
            if ($limit !== null) {
                $request['to'] = ($this->sum ($request['from'], $limit * $this->timeframesMilliseconds[$timeframe]));
            } else {
                $request['to'] = $this->sum ($this->seconds (), 1);
            }
        }
        $response = $this->v4PublicGetCoinCandle (array_merge ($request, $params));
        return $this->parse_ohlcvs($response->candles, $market, $timeframe, $since, $limit);
    }

    public function fetch_orders ($symbol = null, $since = null, $limit = null, $params = array ()) {
        if ($symbol === null)
            throw new ArgumentsRequired ($this->id . ' fetchOrders () requires a $symbol argument');
        $this->load_markets();
        $market = $this->market ($symbol);
        $response = null;
        $response = $this->privatePostListOrders (array_merge (array (
            'coin_pair' => $market['base']
        ), $params));
        $orders = $response['response_data']['orders'];
        for ($i = 0; $i < count ($orders); $i++) {
            $orders[$i] = $this->parse_order($orders[$i]);
        }
        return $orders;
    }

    public function fetch_tickers ($symbols = null, $params = array ()) {
        $tickers = array ();
        for ($i = 0; $i < count ($this->tickers); $i++) {
            $ticker = $this->tickers[$i];
            $tickers[$ticker] = $this->fetch_ticker($ticker);
        }
        return $tickers;
    }

    public function sign ($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $url = $this->urls['api'][$api] . '/';
        $query = $this->omit ($params, $this->extract_params($path));
        if ($api === 'public' || ($api === 'v4Public')) {
            $url .= $this->implode_params($path, $params);
            if ($query)
                $url .= '?' . $this->urlencode ($query);
        } else {
            $this->check_required_credentials();
            $url .= $this->version . '/';
            $nonce = $this->nonce ();
            $body = $this->urlencode (array_merge (array (
                'tapi_method' => $path,
                'tapi_nonce' => $nonce,
            ), $params));
            $auth = '/tapi/' . $this->version . '/' . '?' . $body;
            $headers = array (
                'Content-Type' => 'application/x-www-form-urlencoded',
                'TAPI-ID' => $this->apiKey,
                'TAPI-MAC' => $this->hmac ($this->encode ($auth), $this->encode ($this->secret), 'sha512'),
            );
        }
        return array ( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function request ($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $response = $this->fetch2 ($path, $api, $method, $params, $headers, $body);
        if (is_array ($response) && array_key_exists ('error_message', $response))
            throw new ExchangeError ($this->id . ' ' . $this->json ($response));
        return $response;
    }
